import * as React from 'react';
import { View,Button } from 'react-native';
import { BottomNavigation,TextInput, Text,Subheading } from 'react-native-paper';
import axios from 'axios'

class ShowBooks extends React.Component{
    state={data:[],test:"ok",repos:[]}
    componentWillMount(){
        this.setState({test:this.props.author})
        const apiUrl = 'https://api.github.com/users/Yashrsaxena/repos';
    axios.get(apiUrl).then((repos) => {
      const allRepos = repos.data;
      this.setState({ loading: false, repos: allRepos });
      console.log(this.state);


});
    }
    render(){
        return (
        <Text>{this.props.author}<Text>{this.state.test}</Text>
        { this.state.repos.length>0?this.state.repos.map(function(repo,index){
            return <Text>{repo.name}</Text>
        }
        ):<Text></Text>
    }
        </Text>
        )
    }
}

export default ShowBooks;
