import * as React from 'react';
import { BottomNavigation, Text, Button } from 'react-native-paper';
const MusicRoute = () => <Text>Music</Text>;

const AlbumsRoute = () => <Text>Albums</Text>;

const RecentsRoute = () => <Text>Recents</Text>;

const Navbar = () => {
    const [loggedIn,setLogin]=React.useState(true);
  const [index, setIndex] = React.useState(0);
  var [routes] = React.useState([

    { key: 'home', title: 'Home', icon: 'queue-music' },
    { key: 'issue', title: 'Issue', icon: 'album' },
    { key: 'recents', title: 'Recents', icon: 'history' }

  ]);
  if(!loggedIn){
      [routes] = React.useState([
    { key: 'music', title: 'Music', icon: 'queue-music' },
    { key: 'albums', title: 'Albums', icon: 'album' }

      ]);
  }


  const rendersc=({route,jumpTo})=>{
      switch (route.key){
          case 'home':
              return <MusicRoute />;
            case 'issue':
                return <AlbumsRoute />;

      }
  }
  const renderScene = BottomNavigation.SceneMap({
    home: MusicRoute,
    issue: AlbumsRoute,
    recents: RecentsRoute,
  });

  return (
    <BottomNavigation
      navigationState={{ index, routes }}
      onIndexChange={setIndex}
      renderScene={rendersc}
    />
  );
};

export default Navbar;
