import * as React from 'react';
import { ScrollView, View,SafeAreaView,FlatList,Button } from 'react-native';
import { BottomNavigation,TextInput, Text, Title, Card, Subheading } from 'react-native-paper';
import ShowCard from '../Screens/show_card';
import axios from 'axios'

const DATA = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    title: 'Third Item',
  },
];

class ShowBooks extends React.Component{
    state={data:[],test:"ok",repos:[]}
    componentWillMount(){
        this.setState({test:this.props.author})
        const apiUrl = 'https://api.github.com/users/Yashrsaxena/repos';
    axios.get(apiUrl).then((repos) => {
      const allRepos = repos.data;
      this.setState({ loading: false, repos: allRepos });
      console.log(this.state);


});
    }



    render(){
        const renderItem = ({ item }) => (
    <Text title={item.name} />
  );

        return (
            <SafeAreaView style={styles.container}>
      <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
    </SafeAreaView>
  );






    }
}

export default ShowBooks;
