import * as React from 'react';
import { View,Button } from 'react-native';
import { BottomNavigation,TextInput, Text,Subheading } from 'react-native-paper';
import axios from 'axios'

class ShowBooks extends React.Component{
    state={data:[]}
    componentWillUpdate(){
    const apiUrl = 'https://api.github.com/users/varunbhati904/repos';
    axios.get(apiUrl).then((repos) => {
      const allRepos = repos.data;
      this.setState({ loading: false, data: allRepos });
      console.log(this.state);
    });
}
    render(){
        return <View>{this.props.author}</View>
    }
}

export default ShowBooks;
